//Aqui creamos la actividad con un post a activities. 
import React from "react";
import axios from 'axios'
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, postActivity } from "../actions/index.js";

import style from "./styles/CreateActivity.css";

const CreateActivity = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const countries = useSelector((state) => state.countries);

    const [input, setInput] = useState({
        name: "",
        difficulty: "",
        duration: "",
        seasson: "",
        cca3: [], 
    });

    useEffect(() => {
    dispatch(getCountries());
    }, [dispatch]);

    const handleInputChangeActivity = (e) => {
        e.preventDefault();
        setInput({
            ...input,
            [e.target.name]: e.target.value
            });
    };

    function handleInputChange(e) {
        e.preventDefault();

        setInput({
            ...input,
            [e.target.name]: e.target.value,
        });
    }

    function handleSelect(e) {
        e.preventDefault()
        setInput({...input, [e.target.name]:[...input.cca3, e.target.value]})
    }

    function removeCountry(name){
        setInput({
            ...input,
            countries: input.countries.filter(e => e !== name)
        })
        //const filter = input.countries.filter(el => el !== name)
        //setInput({...input, countries: filter})
    }

    function handleSubmit(e) {
        e.preventDefault();
        if(!input.name|| !input.difficulty|| !input.duration|| !input.seasson|| input.countries.length<=0){
            console.log(input.name)
            console.log(input.difficulty)
            console.log(input.duration)
            console.log(input.seasson)
            console.log(input.countries)
            alert("Missing fields to fill!!")  
        }else{
        dispatch(postActivity(input));
        console.log(input.name)
        console.log(input.difficulty)
        console.log(input.duration)
        console.log(input.seasson)
        console.log(input.countries)
        alert("Activity created!");
        setInput({
            countries: [],
        name: "",
        difficulty: "",
        duration: "",
        seasson: "",
        });
        navigate("/home");
        }
    }

    return (
        <div className="ContainerActivity">
        <Link to={"/home"}>
            <button className="btn1" >Go Back</button>
        </Link>
            <div className="ContainerActivity1">
                <div className="ContainerActivity2">
                    <h3>Add activity:</h3>
                    <form  onSubmit={handleInputChange}>
                        <input
                            onChange={(e)=>handleInputChangeActivity(e)}
                            name="name"
                            value={input.name}
                            type="text"
                            placeholder="Activity name..."
                            className="Creator3"
                        />
                    </form>
                    <form onSubmit={handleSubmit}>
                    <h3>Select Difficulty:</h3>
                    <select className="Selector" name="difficulty" onChange={handleInputChange}>
                        <option disabled={true}>Select</option>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>                    

                    <h3>Select Duration:</h3>
                    <select className="Selector" name="duration" onChange={handleInputChange}>
                        <option disabled={true}></option>
                        <option value="30">30 min</option>
                        <option value="1">1 hour</option>
                        <option value="2">2 hours</option>
                        <option value="3">3 hours</option>
                        <option value="4">4 hours</option>
                        <option value="5">More than 5 hours</option>
                    </select>
                    
                    <h3>Select Seasson:</h3>
                    <select className="Selector" name="seasson" onChange={handleInputChange}>
                        <option disabled={true}>Select</option>
                        <option value="Summer">Summer</option>
                        <option value="Spring">Spring</option>
                        <option value="Fall">Fall</option>
                        <option value="Winter">Winter</option>
                    </select>

                    <h3>Select a Country or Countries: </h3>
                    <select className="Selector" name="countries" onChange={handleSelect}>
                        <option disabled={true}>Select Countries</option>
                        {
                            countries.length > 0 && countries.map(c => (
                                <option itemType="checkbox" key={c.id}>{c.name}</option>
                            ))
                        }
                    </select>

                    <div >
                        <h2>Countries added:</h2>
                        <div>
                        {input.cca3.length > 0 &&
                    input.cca3.map((country, index) => (
                        <div className={style.country} key={index}>
                            <p>{country}</p>
                            <button type='button' onClick={()=> removeCountry(country)}>X</button>
                        </div>
                    ))
                }
                        </div>
                    </div>
                        <button className="btn">Submit activity</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CreateActivity;
