                <label>Difficulty</label>
                <select className={style.select} name='difficulty' defaultValue='Select' onChange={handleChange}>
                    <option disabled={true}>Select</option>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                </select>
