//Aqui creamos la actividad con un post a activities. 
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, postActivity } from "../actions/index.js";

import style from "./styles/CreateActivity.css";

const CreateActivity = () => {
    let dispatch = useDispatch();
    let history = useNavigate();
    let countries = useSelector((state) => state.countries);

    const [input, setInput] = useState({
        name: "",
        dificulty: "",
        duration: "",
        seasson: "",
        cca3: [], 
    });

    useEffect(() => {
    dispatch(getCountries());
    }, [dispatch]);

    const handleInputChangeActivity = (e) => {
        e.preventDefault();
        setInput({
            ...input,
            [e.target.name]: e.target.value
            });
    };

    function handleInputChange(e) {
        setInput({
            ...input,
            [e.name]: e.value,
        });
    }

    function handleSelect(e) {
        setInput({
            ...input,
            cca3: [...input.cca3, e.value,],
        });
    }
    
    function handleSubmit(e) {
        e.preventDefault();
        if(!input.name|| !input.dificulty|| !input.duration|| !input.seasson|| input.cca3.length<=0){
            alert("Missing fields to fill!!")  
        }else{
        dispatch(postActivity(input));
        alert("Activity created!");
        setInput({
        cca3: [],
        name: "",
        dificulty: "",
        duration: "",
        seasson: "",
        });
        history.push("/home");
        }
    }

    return (
        <div className={style.ContainerActivity}>
        <Link to={"/home"}>
            <button className={style.btn1} >Go Back</button>
        </Link>
            <div className={style.ContainerActivity1}>
                <div className={style.ContainerActivity2}>
                    <h3>Add activity:</h3>
                    <form  onSubmit={handleInputChange}>
                    <input
                        onChange={(e)=>handleInputChangeActivity(e)}
                        name="name"
                        value={input.name}
                        type="text"
                        placeholder="Activity..."
                        className="Creator3"
                        pattern="[a-z]{1,15}"
                        title= "Only numbers and leters"
                    />
                    </form>
                    <form onSubmit={handleSubmit}>
                    <h3>Select Dificulty:</h3>
                    <div >
                        <select>
                            <option disabled={true}>Select Dificulty</option>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                        </select>
                        {/*
                            <Select 
                            className="Selector"
                            name="dificulty" 
                            onChange={handleInputChange} 
                            options={optionsDi} 
                            placeholder="Selecct Dificulty"/>
                            */
                        }
                    </div>
                    <h3>Select Duration:</h3>
                    <select className={style.Selector}
                        name="duration" 
                        defaultValue="Select Duration" 
                        onChange={handleInputChange} 
                    >
                        <option disabled={true}>Select Duration</option>
                        <option>30 min</option>
                        <option>1 hour</option>
                        <option>2 hour</option>
                        <option>3 hour</option>
                        <option>4 hour</option>
                        <option>More than 5 hour</option>
                    </select>
                    <h3>Select Seasson:</h3>
                    <div className={style.Selector}>
                        <select                         
                        name="seasson" 
                        defaultValue="Select Seasson" 
                        onChange={handleInputChange} >
                            <option disabled={true}>Selec Seasson</option>
                            <option value="Summer">Summer</option>
                            <option value="Fall">Fall</option>
                            <option value="Winter">Winter</option>
                            <option value="Spring" >Spring</option>
                        </select>
                        {/*
                                                    <Select 
                                                    name="seasson" 
                                                    onChange={handleInputChange} 
                                                    placeholder="Selecct Seasson"  
                                                    options={optionsSe}/>
                                                    */
                                                    
                        }

                    </div>
                    <h3>Select a Country or Countries: </h3>
                    <div className={style.Selector}>
                        <select                         
                        name="countries" 
                        defaultValue="Select Countries" 
                        onChange={handleSelect} >
                            <option disabled={true}>Select Countries</option>
                            {countries.length > 0 && countries.map(c => (
                                <option value={c.cca3} key={c.cca3} onClick={handleSelect}>{c.name}</option>
                            ))}
                        </select>
                        {/*
                                                    <Select 
                                                    placeholder="Selecct one o more Cointries " 
                                                    options={optionsC} 
                                                    onChange={handleSelect}/>
                                                    */
                        }
                    </div>
                    <div >
                        <h2>Countries added:</h2>
                        <div>
                        {input.cca3?.map((e)=>{return <h4 key={e}>{e}</h4>})}
                        </div>
                    </div>
                        <button className={style.btn}>Submit activity</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CreateActivity;
