import React , {useEffect}from 'react';
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getActivities } from "../actions/index";

import style from "./styles/NavBar.module.css";

const NavBar = ({ handlerFilterByRegion, handleSort, handlerFilterByActivity, handleClick }) => {
    const activities = useSelector((state) => state.activities);
    const dispatch = useDispatch();

    const disableOption = () => { // Solucionado PAAAA (Por mi :D)
        document.getElementById("orderBy").options[0].disabled = true;
    };


    useEffect(() => {
        dispatch(getActivities())
    }, [dispatch]);

    return (
        <div className={style.navContainer}>
            <div className={style.creatorAndRefresh}>
                <Link to="/create">
                    <button className={style.Creator}>Activity Creator</button>
                </Link>

                <button className={style.refresh} onClick={handleClick}>Refresh Countries</button> 
            </div>   
            
            <div className={style.filters}> 
                <select className={style.Creator1} onChange={(e)=> handlerFilterByRegion(e)}>
                    <option value="All">All Region</option>
                    <option value="Americas">Americas</option>
                    <option value="Europe">Europe</option>
                    <option value="Africa">Africa</option>
                    <option value="Oceania">Oceania</option>
                    <option value="Asia">Asia</option>
                </select>

                <select id="orderBy" className={style.Creator1} onChange={(e) => handleSort(e)} onClick={disableOption}>
                    <option value="All">Order By</option>
                    <option value="abc-asc">Sort from A to Z</option>
                    <option value="abc-des">Sort from Z to A</option>
                    <option value="pop-asc">Population ↑</option>
                    <option value="pop-des">Population ↓</option>
                </select>
                
                <select className={style.Creator1} onChange={(e)=> handlerFilterByActivity(e)}>
                    <option value="All">All Activities</option>
                    {
                        activities?.map((e) => {
                            return (
                                <option value={e.name} key={e.id}>{e.name}</option>
                            )
                        })
                    }
                </select>
            </div>
        </div>
    );
};

export default NavBar;
